mapper.select:
SELECT 

FROM jmsg_phone a

WHERE a.id = #{id}

SELECT 

FROM jmsg_phone a




		AND a.phone = #{phone}


		AND a.scope = #{scope}


		AND a.create_datetime >= #{createDatetimeQ}


		AND a.create_datetime <= #{createDatetimeZ}




		ORDER BY ${page.orderBy}


	    order by a.create_datetime desc

mapper.update:
UPDATE jmsg_phone SET 	
	phone = #{phone},
	scope = #{scope},
	type = #{type}
WHERE id = #{id}
