mapper.select:
SELECT a.id AS "id", a.task_id AS "taskId", a.customer_order_id AS "customerOrderId", a.phone AS "phone", a.sms_content AS "smsContent", a.user_id AS "userId", a.pay_type AS "payType", a.push_flag AS "pushFlag", a.channel_code AS "channelCode", a.topic AS "topic", a.sp_number AS "spNumber", a.report_gateway_id AS "reportGatewayId" FROM jmsg_sms_send a
a.send_status='P000'
AND a.task_id = #{taskId}
AND a.id > #{id}
ORDER BY a.id LIMIT 1000

SELECT a.user_id AS "userId",id,send_datetime sendDatetime,phone,send_status sendStatus,task_id taskId, a.report_gateway_id AS "reportGatewayId" FROM jmsg_sms_send a
a.send_status LIKE 'F%' AND a.push_flag='9'
AND a.task_id = #{taskId}
ORDER BY a.id LIMIT ${pageNo},${pageSize}

SELECT a.user_id id
from ${tableName} a 
LEFT JOIN sys_user u1 ON a.user_id = u1.id
WHERE a.phone=#{data.destTermID} 
AND a.channel_code=#{data.gateWayID} 
ORDER BY a.id DESC LIMIT 1
