    public byte[] makeMms(String mmsTitle, String mmsSource) {
    	
    	ByteArrayOutputStream out = null;
        try {
            out = new ByteArrayOutputStream();
            MmsHeaders mmsHeader = new MmsHeaders();
            mmsHeader.setMessageType(MmsConstants.X_MMS_MESSAGE_TYPE_ID_M_RETRIEVE_CONF);
            mmsHeader.setFrom(mmsSource + "/TYPE=PLMN");
//            mmsHeader.setSubject(mmsTitle);
            mmsHeader.setTo(mmsSource + "/TYPE=PLMN");
            
            MimeMultipartMixed mimeMultipartMixed = new MimeMultipartMixed();
            
            
            byte[] imageBytes = FileUtils.readFileToByteArray(new File("/Users/mac/Downloads/白鹿原/123.jpeg"));
            String seq = StringUtil.randString(5);
            MimeBodyPart imageBody = MimeFactory.createBinaryBodyPart(imageBytes, "image/jpeg");
            imageBody.setContentId(seq);
            imageBody.setContentLocation(seq);
            mimeMultipartMixed.addBodyPart(imageBody);
            
            String text = FileUtils.readFileToString(new File("/Users/mac/Downloads/白鹿原/文案.txt"));
            seq = StringUtil.randString(5);
            MimeBodyPart mineBody = MimeFactory.createTextBodyPart(text);
            mineBody.setContentId(seq);
            mineBody.setContentLocation(seq);
            mimeMultipartMixed.addBodyPart(mineBody);
            
            byte[] videoBytes = FileUtils.readFileToByteArray(new File("/Users/mac/Downloads/白鹿原/123.mp4"));
            seq = StringUtil.randString(5);
            MimeBodyPart videoBody = MimeFactory.createBinaryBodyPart(videoBytes, "video/mp4");
            videoBody.setContentId(seq);
            videoBody.setContentLocation(seq);
            mimeMultipartMixed.addBodyPart(videoBody);
            
            writeMessageToStream(out, mimeMultipartMixed, mmsHeader);
            out.flush();
            
            return out.toByteArray();
        } catch (Exception e) {
            e.printStackTrace();
        } finally {
            if(out != null) {
                try {
                    out.close();
                } catch (IOException e) {
                    // TODO Auto-generated catch block
                    e.printStackTrace();
                }
            }
        }
        return null;
    }

    /**
     *
     * 彩信制作 任务ID、彩信标题、彩信来源、彩信体 存入缓存，返回
     * 
     * @param taskid
     * @param mmsTitle
     * @param mmsSource
     * @param mmsBody
     * @return 彩信大小
     */
    public byte[] makeMmsPdu(String taskid, String mmsTitle, String mmsSource, String mmsBody) {
        
        ByteArrayOutputStream out = null;
        
        try {
            if(StringUtils.isBlank(mmsBody) || StringUtils.isBlank(taskid))return null;//验证参数
            if(StringUtils.isNotBlank(mmsTitle)) mmsTitle = Encodes.unescapeHtml(mmsTitle);
            
            out = new ByteArrayOutputStream();
            MmsHeaders mmsHeader = new MmsHeaders();
            mmsHeader.setMessageType(MmsConstants.X_MMS_MESSAGE_TYPE_ID_M_RETRIEVE_CONF);
            mmsHeader.setFrom(mmsSource + "/TYPE=PLMN");
            mmsHeader.setSubject(mmsTitle);
            mmsHeader.setTo(mmsSource + "/TYPE=PLMN");
            writeMessageToStream(out, createMmsMime(mmsBody), mmsHeader);
            out.flush();
//          FileUtils.writeByteArrayToFile(new File(Global.getConfig("mms.file.path")+Integer.toHexString(Integer.parseInt(taskid)))
//                  , content);
            return out.toByteArray();
        } catch (Exception e) {
            e.printStackTrace();
        } finally {
            if(out != null) {
                try {
                    out.close();
                } catch (IOException e) {
                    // TODO Auto-generated catch block
                    e.printStackTrace();
                }
            }
        }
        return null;
    }
    
    public static void getTagText(String seq, Node node, MimeMultipartMixed mimeMultipartMixed){
        if ( node instanceof ImageTag) {
            ImageTag imageTag = (ImageTag) node;
            String imageBase64 = imageTag.getAttribute("src");
            int index = imageBase64.indexOf(";base64,");
            imageBase64 = imageBase64.substring(index + 8);
            //BASE64Decoder decoder = new BASE64Decoder();
            final Decoder decoder = getDecoder();
            byte[] bytes = null;
            try {  
                bytes = decoder.decode(imageBase64);
                for (int j = 0; j < bytes.length; ++j) {  
                    if (bytes[j] < 0) {// 调整异常数据  
                        bytes[j] += 256;  
                    }  
                }  
            } catch (Exception e) {
            }  
            
            MimeBodyPart imageBody = MimeFactory.createBinaryBodyPart(bytes, "image/jpeg");
            imageBody.setContentId(seq);
            imageBody.setContentLocation(seq);
            mimeMultipartMixed.addBodyPart(imageBody);
        } else {
            String plainText = node.toPlainTextString();
            if(StringUtils.isNotBlank(plainText)){
                MimeBodyPart mineBody = MimeFactory.createTextBodyPart(Encodes.unescapeHtml(plainText));
                mineBody.setContentId(seq);
                mineBody.setContentLocation(seq);
                mimeMultipartMixed.addBodyPart(mineBody);
            }
        }
    }
    
    
    public MimeMultipartMixed createMmsMime(String mmsBody) throws IOException, ParserException {
        mmsBody = Encodes.unescapeHtml(mmsBody).replaceAll("(?i)<br>", "\n");
        MimeMultipartMixed mimeMultipartMixed = new MimeMultipartMixed();
        Parser parser = Parser.createParser(mmsBody, "UTF-8");
        int idx = 0;
        String seq = StringUtil.randString(5);
        for (NodeIterator i = parser.elements(); i.hasMoreNodes();) {
            Node node = i.nextNode();
            if ( node instanceof Div) {
                if( node.getChildren() != null) {
                    for(SimpleNodeIterator it= node.getChildren().elements(); it.hasMoreNodes();) {
                        Node item = it.nextNode();
                        getTagText(seq + (idx ++), item, mimeMultipartMixed);
                    }
                }
            } else {
                getTagText(seq + (idx ++), node, mimeMultipartMixed);
            }
        }
        return mimeMultipartMixed;
    }
    
    

    private void writeMessageToStream(OutputStream out, MimeBodyPart message, MmsHeaders headers) throws MmsException {
        try {
            // Add headers
            writeHeadersToStream(out, headers);

            // Add content-type
            // Prefer "vnd.wap.multipart..." instead of "multipart/..."
            if (message instanceof MimeMultipart) {
                // Convert multipart headers...
                // TODO: Clone content type... We shouldn't change the msg...
                String ct = message.getContentType().getValue();
                String newCt = WspUtil.convertMultipartContentType(ct);
                message.getContentType().setValue(newCt);
            }

            MmsHeaderEncoder.writeHeaderContentType(WspEncodingVersion.VERSION_1_2, out, message.getContentType());

            // Add content
            MimeEncoder wapMimeEncoder = new WapMimeEncoder(WspEncodingVersion.VERSION_1_3);
            wapMimeEncoder.writeBody(out, message);
        } catch (IOException ex) {
            throw new MmsException("Failed to write message to stream", ex);
        }
    }

    private void writeHeadersToStream(OutputStream out, MmsHeaders headers) throws IOException {
        MmsHeaderEncoder.writeHeaderXMmsMessageType(out, headers.getMessageType());
        MmsHeaderEncoder.writeHeaderXMmsTransactionId(out, headers.getTransactionId());
        MmsHeaderEncoder.writeHeaderXMmsMmsVersion(out, headers.getVersion());
        writeHeaderDate(out, new Date());
        if (headers.getFrom() != null) {
            writeMmsHeaderFrom(out, headers.getFrom());
        }

        if (headers.getTo() != null) {
            writeMmsHeaderTo(out, headers.getTo());
        }

        if (headers.getSubject() != null) {
            writeMmsHeaderSubject(out, headers.getSubject());
        }
        writeHeaderXMmsDeliveryReport(out, MmsConstants.X_MMS_READ_REPLY_ID_NO);
    }

    private void writeHeaderXMmsDeliveryReport(OutputStream os, int deliveryReportId) throws IOException {
        WspUtil.writeShortInteger(os, MmsConstants.HEADER_ID_X_MMS_DELIVERY_REPORT);
        WspUtil.writeShortInteger(os, deliveryReportId);
    }

    private void writeMmsHeaderFrom(OutputStream os, String from) throws IOException {
        WspUtil.writeShortInteger(os, MmsConstants.HEADER_ID_FROM);
        if (from == null) {
            from = "Unknow";
        } else {
            ByteArrayOutputStream baos = new ByteArrayOutputStream();

            // Write data to baos
            WspUtil.writeShortInteger(baos, MmsConstants.FROM_ADDRESS_PRESENT);
            MmsHeaderEncoder.writeEncodedStringValue(baos, from);
            WspUtil.writeValueLength(os, baos.size());
            os.write(baos.toByteArray());
        }
    }

    private void writeMmsHeaderTo(OutputStream os, String receiver) throws IOException {
        WspUtil.writeShortInteger(os, MmsConstants.HEADER_ID_TO);
        MmsHeaderEncoder.writeEncodedStringValue(os, receiver);
    }

    private void writeMmsHeaderSubject(OutputStream os, String subject) throws IOException {
        WspUtil.writeShortInteger(os, MmsConstants.HEADER_ID_SUBJECT);
        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        baos.write(0xEA);
        MmsHeaderEncoder.writeEncodedStringValue(baos, subject);
        baos.close();
        WspUtil.writeValueLength(os, baos.size());
        os.write(baos.toByteArray());
    }

    private void writeHeaderDate(OutputStream os, Date date) throws IOException {
        WspUtil.writeShortInteger(os, MmsConstants.HEADER_ID_DATE);
        long time = date.getTime() / 1000;
        WspUtil.writeLongInteger(os, time);
    }
