mapper.select:
SELECT 

FROM jmsg_addresslist_group a

WHERE a.id = #{id}

SELECT 

FROM jmsg_addresslist_group a


	AND a.status='1'

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.create_by = #{userId}


		AND a.company_id = #{companyId}


		AND a.parent_id=#{parent.id}


		AND  (a.id=#{id} or a.parent_ids LIKE CONCAT(#{parentIds},#{id},',%'))




		ORDER BY ${page.orderBy}
